import {useEffect,useState} from "react" ;
import Map from './Map';
function Label() {
 const [name,setNames]=useState([]);
const [mapAddress,setMapAddress]=useState("");
const setAddress=(e)=>{
    if(e){
            const n=name.filter((n)=>{
                    return n.id===e
                })
                setMapAddress(n.address);
            }
            else{
                setMapAddress("");
            }
}

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
    console.log(data);
    }
    catch(e){
        setNames([]);
        console.log(e);
    }
}
console.log(MapAddress);
getData();
},[mapAddress])
  return (
     

    <form>
    <label htmlFor="name">Select a  User</label>
    <select id="names" onChange={(e)=>setAddress(e.target.value)}>
    {
        name.length===0 ? <option value="nothing">Loading...</option>:
        name.map((item)=>{
            return <option value={item.id}>{item.name}</option> 
        }) 
    }
  
        </select>
    <Map></Map>
    </form>
   
  )
  
}

export default Label
