import React, { useReducer } from 'react'
import {useEffect,useState} from "react" ;
import Map from './Map';

const reducer=(state,action)=>{
 switch(action.type){
     case "Loading_Error":
         return state=false;
  
     default:
     return state;
 }
}
function Label() {
const [error,setError]=useReducer(reducer,true);
 const [name,setNames]=useState([]);
 const [add,setadd]=useState();
const [mapAddress,setMapAddress]=useState("");
const setAddress=(e)=>{
    if(e){
            const n=name.filter((na)=>{
                 if(na.id==e){
                        return na.id;
                    }
                })
                setMapAddress(n[0].address);
            }
            else{
                setMapAddress("");
            }
}

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
    console.log(data);
     setError({type:"Loading_Error"});
    }
    catch(e){
        console.log(e.message);
        setNames([]);
        
    }
}

getData();
},[])
  return (
     

<div className='labels'>
    <label htmlFor="name" className='labels-title'>Select a  User</label>
    <select id="names" onChange={(e)=>setAddress(e.target.value)}>
      {name.length>1 && <option value="none"  >Please Select a User</option> }
         {name.map((item)=>{
             return  <option value={item.id} key={item.id}>{item.name}</option> 
            })
        }
        
    
        
        
        
        
    
    
    
    
  
        </select>
        {error && <p className='labels-loading'>Loading....</p>}
        {!error && <button onClick={()=>setAddress,a}>Show Address</button>}
       <Map mapAddress={mapAddress} error={error}></Map>
    </div>
  
   
  )
  
}

export default Label
