import React, { useReducer } from 'react'
import {useEffect,useState} from "react" ;
import Map from './Map';

function Label() {
const [error,setError]=useState(true);
 const [name,setNames]=useState([]);
 const [map,showMap]=useState(false);
 const [id,setId]=useState("");
const [mapAddress,setMapAddress]=useState("");
const setAddress=()=>{
   console.log(id);
    if(id){
    if(id==="none"){
       setError(prev=>{
         return {...prev,msg:"Please Select Something"};
       })
       return;
    }
        const n=name.filter((na)=>{
            if(na.id==id){
                return na.id;
            }
        })
        setMapAddress(n[0].address);
        showMap(true);
            }
            else{
                setMapAddress("");
            }
}

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
    setAddress();
    console.log(data);
     setError(false);
    }
    catch(e){
        console.log(e.message);
        setNames([]);
        
    }
}

getData();
},[])
  return (
     

<div className='labels'>
    <label htmlFor="name" className='labels-title'>Select a  User</label>
    <select id="names" defaultValue={"none"} onChange={(e)=>setId(e.target.value)}>
      {name.length>1 && <option  defaultValue={"none"} >Please Select a User</option> }
         {name.map((item)=>{
             return  <option value={item.id} key={item.id}>{item.name}</option> 
            })
        }
        
    
        
        
        
        
    
    
    
    
  
        </select>
        {error && <p className='labels-loading'>Loading....</p>}
        {!error && <button onClick={()=>setAddress()} className="show">Show Address</button>}
       { map && <Map mapAddress={mapAddress} error={error}></Map>}
    </div>
  
   
  )
  
}

export default Label
