import React from 'react'
import {useEffect,useState} from "react" ;
import Map from './Map';

function Label() {
const [error,setError]=useState({errormsgvalue:true,msg:"Loading...",errorbtnvalue:true});
 const [name,setNames]=useState([]);
 const [map,showMap]=useState(false);
 const [id,setId]=useState("");
const [mapAddress,setMapAddress]=useState("");
const setAddress=()=>{
//    console.log(id);
    if(id){
  
        const n=name.filter((na)=>{
            if(na.id==id){
                return na.id;
            }
        })
        setMapAddress(n[0].address);
        showMap(true);
            }
            else{
                setMapAddress("");
            }
}

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
    setAddress();
    console.log(data);
     setError(false);
    }
    catch(e){
        console.log(e.message);
        setNames([]);
        
    }
}

getData();
},[])
  return (
     

<div className='labels'>
    <label htmlFor="name" className='labels-title'>Select a  User</label>
    <select id="names"  onChange={(e)=>setId(e.target.value)}>
      {name.length>1 && <option  defaultValue={"none"} >Please Select a User</option> }
         {name.map((item)=>{
             return  <option value={item.id} key={item.id}>{item.name}</option> 
            })
        }
        
    
        
        
        
        
    
    
    
    
  
        </select>
        {error.errormsgvalue && <p className='labels-loading'>{error.msg}</p>}
        {!error.value && <button onClick={()=>setAddress()} className="showMapBtn">Show Address</button>}
       { !error.value && map && <Map mapAddress={mapAddress}></Map>}
    </div>
  
   
  )
  
}

export default Label
