import React from 'react'
import {useEffect,useState} from "react" ;
import Map from './Map';
function Label() {
 const [name,setNames]=useState([]);
 const [address,setAddress]=useState(()=>(e)=>{
    if(e){
    const n=name.filter((n)=>{
            return n.id===e
        })
        setAddress(n.address);
    }
    else{
        setAddress("");
    }
 });

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
    console.log(data);
    }
    catch(e){
        setNames([]);
        console.log(e);
    }
}
console.log(address);
getData();
},[address])
  return (
     

    <form>
    <label htmlFor="name">Select a  User</label>
    <select id="names" onChange={(e)=>setAddress(e.target.value.address.cit)}>
    {
        name.length===0 ? <option value="nothing">Loading...</option>:
        name.map((item)=>{
            return <option value={item}>{item.name}</option> 
        }) 
    }
  
        </select>
    <Map></Map>
    </form>
   
  )
  
}

export default Label
