import React from 'react'
import {useEffect,useState} from "react" ;
import Map from './Map';
function Label() {
const [error,setError]=useRed
 const [name,setNames]=useState([]);
const [mapAddress,setMapAddress]=useState("");
const setAddress=(e)=>{
    if(e){
            const n=name.filter((na)=>{
                 if(na.id==e){
                        return na.id;
                    }
                })
                setMapAddress(n[0].address);
            }
            else{
                setMapAddress("");
            }
}

useEffect(()=>{
const getData=async()=>{
    try{
    const response=await fetch(`https://jsonplaceholder.typicode.com/users`);
    const data=await response.json();
    setNames(data);
  
    }
    catch(e){
        setNames([]);
        
    }
}

getData();
},[])
  return (
     

<div className='form'>
    <label htmlFor="name">Select a  User</label>
    <select id="names" onChange={(e)=>setAddress(e.target.value)}>
        <option value="none">Please Select  A  User </option> 
    {
        
        name.map((item)=>{
            return <option value={item.id} key={item.id}>{item.name}</option> 
        })
        
        
        
        
    } 
      
    
  
        </select>
    <Map mapAddress={mapAddress}></Map>
    </div>
  
   
  )
  
}

export default Label
